
import React  from 'react';
import PropTypes from 'prop-types';

import CUI_CONST from './CiscoUIConstants.jsx';
import warning from 'fbjs/lib/warning';


class Gauge extends React.Component {

    render() {
        return (
            <div className="gauge-container">
                <div
                    className={`gauge gauge--alt gauge--${this.props.size} gauge--${this.props.color}`}
                    data-percentage={this.props.percent}
                >
                    <div className="gauge__circle">
                        <div className="mask full">
                            <div className="fill" />
                        </div>
                        <div className="mask half">
                            <div className="fill" />
                            <div className="fill fix" />
                        </div>
                    </div>
                    <div className="gauge__inset">
                        <div className="gauge__percentage">
                            {Math.floor(this.props.percent)}
                        </div>
                    </div>
                </div>
                {
                    this.children &&
                    <div className="gauge--label">ppp{this.children}</div>
                }

            </div>
        )
    }
}

Gauge.defaultProps = {
    size: 'small',
    percent: 0,
    color: 'default',
};

Gauge.propTypes = {
    // id: PropTypes.string,
    // name: PropTypes.string,
    // disabled: PropTypes.bool,
    // required: PropTypes.bool,
    // value: PropTypes.oneOfType([
    //     PropTypes.number,
    //     PropTypes.string,
    // ]),
    percent: PropTypes.number,
    size: PropTypes.oneOf(CUI_CONST.GAUGE_SIZES),
    color: PropTypes.oneOf(CUI_CONST.BASE_COLORS),
};

export default Gauge;