import React  from 'react';
import PropTypes from 'prop-types';
import CUI_CONST from './CiscoUIConstants.jsx';

class Header extends React.Component {
    render() {
        let buttonClasses = [];

        if(this.props.buttons !== null){
            this.props.buttons.map(function(button){
                buttonClasses.push(`icon-${button}`);
            });
        }

        return (
            <header className="header">
                <div className="header-bar container">
                    <a className="btn btn--small btn--icon toggle-menu">
                        <span className="icon-list-menu"></span>
                    </a>
                    <a className="header-bar__logo">
                        <span className="icon-cisco"></span>
                    </a>
                    <div className="header-bar__main">
                        <div className="header-heading">
                            <h1 className="page-title">{this.props.title}</h1>
                        </div>
                    </div>
                    <div className="header-toolbar">
                        {buttonClasses.map(function(button){
                            return (
                                <a className="btn btn--small btn--icon">
                                    <span className={button}></span>
                                </a>
                            )
                        })}
                    </div>
                </div>
            </header>
        )
    }
}

Header.propTypes = {
    title: PropTypes.string,
    buttons: PropTypes.arrayOf(PropTypes.string),
};

Header.defaultProps = {
    title: "Page Title",
    buttons: null,
};

export default Header;