
import React from 'react';
import PropTypes from 'prop-types';

import CUI_CONST from './CiscoUIConstants.jsx';

class Icon extends React.Component {

    render() {
        let classes = [];

        classes.push(`icon-${this.props.icon}`);

        if(this.props.size){
            classes.push(`icon-${this.props.size}`);
        }

        return (
            <span className={classes.join(' ')} title={this.props.title} />
        );
    }
}

Icon.defaultProps = {
    icon: '',
    size: null,
    title: null,
};

Icon.propTypes = {
    icon: PropTypes.string,
    size: PropTypes.oneOf(CUI_CONST.ICON_SIZES),
    title: PropTypes.string,
};

export default Icon;