
import React  from 'react';

class Li extends React.Component {
    render() {

        // const { href, ...passthru_props } = this.props;
        const href = this.props.href;
        const passthru_props = this.props;


        return (
            <li {...passthru_props}>
                {href ? (
                    <a href={href}>{this.props.children}</a>
                ) : (
                    this.props.children
                )}
            </li>
        )
    }
}


export class List extends React.Component {
    render() {
        return (
            <ul className={`list`} {...this.props} {...this.state}>
                {this.props.children}
            </ul>
        );
    }
}



export default Li