
import React  from 'react';
import PropTypes from 'prop-types';

import CUI_CONST from './CiscoUIConstants.jsx';
import warning from 'fbjs/lib/warning';


class Loader extends React.Component {

    render() {
        return (
            <div>
                {this.props.label &&
                    <h4 className="text-center">{this.props.label}</h4>
                }
                <div className={`loading-dots loading-dots--${this.props.color}`}>
                    <span />
                    <span />
                    <span />
                </div>
            </div>
        )
    }
}

Loader.defaultProps = {
    color: 'muted',
};

Loader.propTypes = {
    color: PropTypes.oneOf(CUI_CONST.LOADER_COLORS),
};

export default Loader;